mapper.select:
SELECT user_id AS userId, kakao_id AS kakaoId, name, email, state, role
FROM users
WHERE kakao_id = #{kakaoId}

mapper.update:
UPDATE users
SET state = true
WHERE kakao_id = #{kakaoId}

UPDATE users
SET state = false
WHERE kakao_id = #{kakaoId}

mapper.select:
SELECT user_id AS userId, kakao_id AS kakaoId, name, email, state, role
FROM users
WHERE user_id = #{userId}
